mapper.select:
SELECT id, email, name, created_at
FROM "User"
WHERE id = #{id}

mapper.update:
UPDATE "User"
SET email = #{email},
    name  = #{name}
WHERE id = #{id}

mapper.sql:
SELECT
b.title as title1,
FROM bookstore.book b LEFT JOIN bookstore.book_order s ON s.book_id = b.id
AND s.order_date >= ${startDate}
AND s.order_date >= ${endDate}
GROUP BY b.id, b.title order by amount desc

WITH _OLD_DATA as (
    select bk$.*
    from bookstore.book bk$
    where ${filter}
), _NEW_DATA as (
    update bookstore.book bk$ SET
        ${record}
    from _OLD_DATA
    where bk$.id = _OLD_DATA.id
    returning *
)
